const fs = require('fs');

let state = {};
try {
  state = JSON.parse(fs.readFileSync('/home/node/.n8n/music-stream/state.json', 'utf8'));
} catch(e) {
  state = { isActive: false };
}

let mood = {};
try {
  mood = JSON.parse(fs.readFileSync('/home/node/.n8n/music-stream/mood.json', 'utf8'));
} catch(e) {
  mood = { style: 'A gentle lofi hip-hop beat with warm vinyl crackle, soft Rhodes piano chords, and a laid-back boom-bap drum pattern, Mellow bass hums underneath as ambient textures float in and out, 432Hz', title: 'Late Night Drift', genre: 'lofi', energy: 'medium' };
}

return [{
  json: {
    isActive: state.isActive || false,
    liveChatId: state.liveChatId || null,
    nextPageToken: state.nextPageToken || null,
    currentMood: JSON.stringify(mood),
    broadcastId: state.broadcastId || null
  }
}];

const fs = require('fs');
const response = $input.first().json;

// Save nextPageToken to state.json for next poll
try {
  const statePath = '/home/node/.n8n/music-stream/state.json';
  const tmpPath = statePath + '.tmp';
  const state = JSON.parse(fs.readFileSync(statePath, 'utf8'));
  state.nextPageToken = response.nextPageToken || state.nextPageToken;
  fs.writeFileSync(tmpPath, JSON.stringify(state, null, 2));
  fs.renameSync(tmpPath, statePath);
} catch(e) { /* ignore state write errors */ }

// Extract and filter chat messages
const items = response.items || [];
const messages = items
  .map(item => ({
    author: item.authorDetails?.displayName || 'Unknown',
    message: item.snippet?.displayMessage || '',
    time: item.snippet?.publishedAt || ''
  }))
  .filter(m => m.message.trim().length > 1)
  .slice(-20);

// Format messages for LLM
const formattedMessages = messages
  .map(m => `${m.author}: ${m.message}`)
  .join('\n');

// Get current style for context
const currentMood = $node['Read Stream State'].json.currentMood;

return [{
  json: {
    messageCount: messages.length,
    hasMessages: messages.length > 0,
    formattedMessages,
    currentMood,
    chatInput: `Current music style: ${currentMood}\n\nRecent YouTube live chat messages:\n${formattedMessages}\n\nBased on these chat messages, decide the style for the next AI-generated instrumental track. Return ONLY valid JSON.`
  }
}];

