const fs = require('fs');

const llmResponse = $input.first().json;

// The chain output is in the 'text' or 'output' field
const responseText = llmResponse.text || llmResponse.output || JSON.stringify(llmResponse);

// Parse JSON from LLM response
let newMood;
try {
  const jsonMatch = responseText.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    newMood = JSON.parse(jsonMatch[0]);
  } else {
    throw new Error('No JSON found in response');
  }
} catch(e) {
  // If parsing fails, keep current mood
  try {
    newMood = JSON.parse(fs.readFileSync('/home/node/.n8n/music-stream/mood.json', 'utf8'));
  } catch(e2) {
    newMood = { style: 'A gentle lofi hip-hop beat with warm vinyl crackle, soft Rhodes piano chords, and a laid-back boom-bap drum pattern, Mellow bass hums underneath as ambient textures float in and out, 432Hz', title: 'Late Night Drift', genre: 'lofi', energy: 'medium' };
  }
}

// Validate required fields
const validMood = {
  style: (typeof newMood.style === 'string' && newMood.style.length > 10) ? newMood.style : 'A gentle lofi hip-hop beat with warm vinyl crackle, soft Rhodes piano chords, and a laid-back boom-bap drum pattern, 432Hz',
  title: (typeof newMood.title === 'string' && newMood.title.length > 0) ? newMood.title.substring(0, 100) : 'Untitled Track',
  genre: newMood.genre || 'lofi',
  energy: ['low', 'medium', 'high'].includes(newMood.energy) ? newMood.energy : 'medium',
  updatedAt: new Date().toISOString()
};

// Atomic write
const moodPath = '/home/node/.n8n/music-stream/mood.json';
const tmpPath = moodPath + '.tmp';
fs.writeFileSync(tmpPath, JSON.stringify(validMood, null, 2));
fs.renameSync(tmpPath, moodPath);

return [{ json: { success: true, mood: validMood } }];
